function init(){animationSetup(),sliderSetup(),alert("init")}function sliderSetup(){$(".timeScaleSlider").slider({min:.1,max:2.1,step:.1,value:1,stop:function(e,a){tl.kill(),interval=a.value,updateTimeline()}}).slider("float",{prefix:"$"}),$(".overlapSlider").slider({min:0,max:1.05,step:.05,value:.5,stop:function(e,a){tl.kill(),overlapFactor=a.value,updateTimeline()}}).slider("float",{handle:!1,labels:!1,prefix:"$"})}function animationSetup(){tl.from(".t1",interval,{x:-50,alpha:0,transformOrigin:"50% 50%",ease:Power2.easeOut},interval-interval*overlapFactor).from(".t2",interval,{x:50,alpha:0,transformOrigin:"50% 50%",ease:Power2.easeOut},2*interval-2*interval*overlapFactor).from(".t3",interval,{x:-100,alpha:0,transformOrigin:"50% 50%",ease:Power2.easeOut},3*interval-3*interval*overlapFactor).from(".t4",interval,{x:-1e3,alpha:0,transformOrigin:"50% 50%",ease:Power2.easeOut},4*interval-4*interval*overlapFactor).from(".t5",interval,{x:1e3,alpha:0,transformOrigin:"50% 50%",ease:Power2.easeOut},5*interval-5*interval*overlapFactor)}function updateTimeline(){TweenMax.to(".tGroup",0,{x:0,alpha:1,ease:Power0.easeNone}),tl=new TimelineMax({paused:!1,yoyo:!1}),tl.from(".t1",interval,{x:-2e3,alpha:0,transformOrigin:"50% 50%",ease:Power3.easeOut},interval-interval*overlapFactor).from(".t2",interval,{x:2e3,alpha:0,transformOrigin:"50% 50%",ease:Power3.easeOut},2*interval-2*interval*overlapFactor).from(".t3",interval,{x:-2e3,alpha:0,transformOrigin:"50% 50%",ease:Power3.easeOut},3*interval-3*interval*overlapFactor).from(".t4",interval,{x:-2e3,alpha:0,transformOrigin:"50% 50%",ease:Power3.easeOut},4*interval-4*interval*overlapFactor).from(".t5",interval,{x:2e3,alpha:0,transformOrigin:"50% 50%",ease:Power3.easeOut},5*interval-5*interval*overlapFactor)}var interval=1,overlapFactor=.8,tl=new TimelineMax({paused:!1,yoyo:!0});